    result.max = Math.max(result.max, value.max);
    return result;
  }, {avg: null, max: 0});
  return {
    average: responseTimes.avg === null ? null : responseTimes.avg / Math.max(_.size(times), 1),
    max: responseTimes.max
  };
}

