'use strict';

var esqueueEvents = require('esqueue/lib/constants/events');

var _require = require('./constants');

var JOBTYPES = _require.JOBTYPES;

var workersFactory = require('./workers');
var oncePerServer = require('./once_per_server');

function createWorkersFactory(server) {
  var queueConfig = server.config().get('xpack.reporting.queue');
  var workers = workersFactory(server);

  // Once more document types are added, this will need to be passed in
  return function registerWorkers(queue) {

    var workerTypes = [JOBTYPES.PRINTABLE_PDF];

    workerTypes.forEach(function (workerType) {
      var log = function log(msg) {
        server.log(['reporting', 'worker', 'debug'], workerType + ': ' + msg);
      };

      log('Registering ' + workerType + ' worker');
      var workerFn = workers[workerType];
      var workerOptions = {
        interval: queueConfig.pollInterval
      };
      var worker = queue.registerWorker(workerType, workerFn, workerOptions);

      worker.on(esqueueEvents.EVENT_WORKER_COMPLETE, function (res) {
        return log('Worker completed: (' + res.job.id + ')');
      });
      worker.on(esqueueEvents.EVENT_WORKER_JOB_EXECUTION_ERROR, function (res) {
        return log('Worker error: (' + res.job.id + ')');
      });
      worker.on(esqueueEvents.EVENT_WORKER_JOB_TIMEOUT, function (res) {
        return log('Job timeout exceeded: (' + res.job.id + ')');
      });
    });
  };
